// https://play.kotlinlang.org/#

enum class Nivel { BASICO, INTERMEDIARIO, AVANCADO }

data class Usuario(val nome: String) {
    companion object {

    }
}

data class ConteudoEducacional(val nome: String, val duracao: Int, val nivel: Nivel)

data class Formacao(var nome: String, var conteudos: List<ConteudoEducacional>) {
    
    companion object {

    }
    
    val inscritos = mutableListOf<Usuario>()
    
    fun matricular(vararg usuario: Usuario) {
        usuario.forEach{inscritos.add(it)}
    }
}

fun main() {

    val nivel = Nivel.BASICO
	val messageNivel = when (nivel) {
    	Nivel.BASICO -> "Basico"
		Nivel.INTERMEDIARIO -> "Intermediario"
		Nivel.AVANCADO -> "Avancado"
    }
    
    val conteudos: List<ConteudoEducacional> = listOf(ConteudoEducacional("Introdução a Kotlin", 3, nivel=Nivel.BASICO),
    												  ConteudoEducacional("Controle de Fluxo", 4, nivel=Nivel.INTERMEDIARIO),
    												  ConteudoEducacional("Programação Orientada a Objetos", 10, nivel=Nivel.AVANCADO),
                                                      ConteudoEducacional("Funções", 4, nivel=Nivel.AVANCADO)
                                                     )

    Formacao(nome = "Kotlin Experience", conteudos).matricular(Usuario("Flavia"),
                                                        Usuario("Marcos"))
    
    println("Na formação ${Formacao} temos os conteudos: ") 
    println("${conteudos.map{it.nome}.joinToString()} com a duração de ${conteudos.map{it.duracao}} horas e de nível ${conteudos.map{it.nivel}}")
    println("E os alunos $Usuario matriculados")
    
